from pages.homepage import HomePagefrom pages.product import ProductPageimport time#Тест открытия ссылки Samsung Galaxy S6def test_open_galaxyS6(driver):    homepage = HomePage(driver)    homepage.open()    homepage.click_galaxy_s6()    product_page = ProductPage(driver)    product_page.check_title_is('Samsung galaxy s6')#Тест открытия monitors и проверка, что там два элементаdef test_two_monitors(driver):    homepage = HomePage(driver)    homepage.open()    homepage.click_monitor()    time.sleep(2)    homepage.check_products_count(2)